      out.write("\t\t\t\t\t\t\t\t<p>\r\n");
      out.write("\t\t\t\t\t\t\t\t\tCustomer awareness and education on Internet banking security\r\n");
      out.write("\t\t\t\t\t\t\t\t\twas a point of emphasis in the guidance published by the FFIEC\r\n");
