      out.write("\t\t\t\t\t\t\t\t\t\tcharacters. Every additional character exponentially\r\n");
      out.write("\t\t\t\t\t\t\t\t\t\tstrengthens a password.</li>\r\n");
      out.write("\t\t\t\t\t\t\t\t\t<li><strong>Avoid using obvious passwords</strong> such\r\n");
      out.write("\t\t\t\t\t\t\t\t\t\tas:\r\n");
